import React, { Component, Fragment } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import "./styles/Confirmation.scss";

import { Redirect } from "react-router-dom";

class Confirmation extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      surename: ""
    };
  }

  handleChange = event => {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  };

  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to="/" />;
    }
  };

  render() {
    return (
      <div>
        <Header />

        <Fragment>
          <div className="box">
            <div className="phases-box">
              <div className="state-box">
                <p className="state-phase-check">
                  <i class="fas fa-check"></i>
                </p>
                <p className="name-phase">MIS DATOS</p>
              </div>

              <div className="state-box">
                <p className="state-phase-pay">2</p>
                <p className="name-phase">PAGO</p>
              </div>
            </div>

            <form>
              <div className="payment-box">
                <div className="text-info">
                  <h1>Pago del producto</h1>
                  <p>
                    Una vez efectuado el pago del producto, recibir√°s un email
                    con los detalles de la compra.
                  </p>
                </div>
                <div>
                  <div>
                    <i className="far fa-credit-card"></i>
                  </div>
                  <div>
                    <button
                      className="button-form"
                      onClick={() => this.renderRedirect()}
                    >
                      Continuar <i class="far fa-arrow-alt-circle-right"></i>
                    </button>
                  </div>
                </div>
              </div>
            </form>
          </div>
        </Fragment>
        <Footer />
      </div>
    );
  }
}

export default Confirmation;
